import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 1,
    tag: ["a", "b", "c"],
  };

  styles = {
    fontSize: 15,
  };

  renderTags() {
    if (this.state.tag.length === 0) return <p>No Tags Found</p>;
    return (
      <ul>
        {this.state.tag.map((t) => (
          <li key={t}>{t}</li>
        ))}
      </ul>
    );
  }

  render() {
    return <React.Fragment>{this.renderTags()}</React.Fragment>;
  }

  getBadgeClasses() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "danger" : "warning";
    return classes;
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
